:host {
  --led-height: 5rem;
  --led-width: 15%;
}
.knob {
  position: absolute;
  left:50%;
  top: 50%;
  width: 24rem;
  height: 24rem;
  margin-left: -12rem;
  margin-top: -12rem;
  border-radius: 50%;
  background: var(--color-widget);
  opacity: .75
}
.led {
  position: absolute;
  width: 100%;
  height: var(--led-height);
  top: 50%;
  margin-top: calc(-0.5 * var(--led-height));
}
.led:after, .led:before {
  content: "";
  position: absolute;
  width: var(--led-width);
  height: var(--led-height);
  left:5rem;top:0;
  background: red;
  border-radius: 50%;
  opacity: var(--led-opacity);
}
.led:before {
  background: var(--color-widget);
  opacity: 0.25;
}
.led-0 {
  transform: rotate(-90deg);
  margin-top: -5rem;

}
.led-0:after, .led-0:before {
  width: 6rem;
  height: 6rem;
}


